import React from 'react';
import {Col, Container, Row, Table} from 'react-bootstrap'
import { useEffect } from 'react';
import { useState } from 'react';
import { GetAll } from '../../apis/crud';
import FullScreenLoader from '../common/FullScreenLoader'

const GetAllData = () => {
  const [data,setData] = useState([])

      useEffect(()=>{

        GetAll().then((res)=>{
          setData(res)

        })

      },[])


     if(data.length>0){
      return (
        <div>
          <Container>
             <Row>
                <Col md={7}>
                <Table striped bordered hover>
                  <thead>
                    <tr style={{fontSize:"18px"}}>
                      <th>Product Name</th>
                      <th>Product Code</th>
                      <th>Image</th>
                      <th>Unit Price</th>
                      <th>Qty</th>
                      <th>Total Price</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>1</td>
                      <td>Mark</td>
                      <td>Otto</td>
                      <td>@mdo</td>
                    </tr>
                  </tbody>
            </Table>
                </Col>
             </Row>
          </Container>
           
        </div>
      )
       
     }else{
      
      return (
        <div>
            <FullScreenLoader/>
        </div>
     )
     }
 
}

export default GetAllData
