import cogoToast from 'cogo-toast'
class ValidationHelper{
    isEmpty(value){
        if(value.length ===0){
            return true
        }else{
            return false
        }
    }

    SuccessToast(msg){
        cogoToast.success(msg)
    }
    EToast(msg){
        cogoToast.success(msg)
    }
}

export const {isEmpty}=ValidationHelper;
